import React, { useState } from 'react';
import './TranscribeText.css';
import PaidNavbar from './PaidNavbar';
import Footer from './Footer';


const TranscriptionApp = () => {
    const [inputText, setInputText] = useState('');
    const [outputText, setOutputText] = useState('');
    const [usageCount, setUsageCount] = useState(0);
    const usageLimit = 3;

    const handleTranscribe = () => {
        if (usageCount < usageLimit) {
            // Add your transcription logic here
            setOutputText(inputText); // This is a placeholder, replace with actual transcription logic
            setUsageCount(usageCount + 1);
        } else {
            alert('Usage limit reached');
        }
    };

    return (
        <div className="transcription-app-container">
            <PaidNavbar />
            <div className="usage-limit">Usage Limit {usageCount}/{usageLimit}</div>
            <div className="input-output-container">
                <div className="input-container">
                    <label>Input</label>
                    <textarea 
                        value={inputText}
                        onChange={(e) => setInputText(e.target.value)}
                        placeholder="Enter text"
                    />
                    <div className="word-count">Words {inputText.split(' ').filter(Boolean).length}/280</div>
                </div>
                <button 
                    onClick={handleTranscribe}
                    className="transcribe-button"
                    disabled={usageCount >= usageLimit}
                >
                    Transcribe âž”
                </button>
                <div className="output-container">
                    <label>Output</label>
                    <textarea 
                        value={outputText}
                        readOnly
                    />
                </div>
            </div>
        </div>
    );
};

export default TranscriptionApp;
