import React, { useState } from 'react';
import './TranscribeText.css';
import PaidNavbar from './PaidNavbar';

const TranscriptionApp = () => {
    const [inputText, setInputText] = useState('');
    const [outputText, setOutputText] = useState('');
    const [usageCount, setUsageCount] = useState(0);
    const usageLimit = 3;

    const handleTranscribe = () => {
        if (usageCount < usageLimit) {
            // Add your transcription logic here
            setOutputText(inputText); // This is a placeholder, replace with actual transcription logic
            setUsageCount(usageCount + 1);
        } else {
            alert('Usage limit reached');
        }
    };

    return (
        <div className="transcribeAppContainer">
            <div className="transcribeUsageLimit">Usage Limit {usageCount}/{usageLimit}</div>
            <div className="transcribeInputOutputContainer">
                <div className="transcribeInputContainer">
                    <label>Input</label>
                    <textarea 
                        value={inputText}
                        onChange={(e) => setInputText(e.target.value)}
                        placeholder="Enter text"
                    />
                    <div className="transcribeWordCount">Words {inputText.split(' ').filter(Boolean).length}/280</div>
                </div>
                <button 
                    onClick={handleTranscribe}
                    className="transcribeButton"
                    disabled={usageCount >= usageLimit}
                >
                    Transcribe âž”
                </button>
                <div className="transcribeOutputContainer">
                    <label>Output</label>
                    <textarea 
                        value={outputText}
                        readOnly
                    />
                </div>
            </div>
        </div>
    );
};

export default TranscriptionApp;
